function escapeTelegramMarkdownV2(text) {
    if (text === null || text === undefined) return '';
    const textStr = String(text);
    const escapeChars = [
        '_',
        '*',
        '[',
        ']',
        '(',
        ')',
        '~',
        '`',
        '>',
        '#',
        '+',
        '-',
        '=',
        '|',
        '{',
        '}',
        '.',
        '!'
    ];
    return textStr.split('').map((char)=>escapeChars.includes(char) ? `\\${char}` : char).join('');
}
